import React, { useState } from 'react';
import SearchBar from '../components/SearchBar';
import SpaceModal from '../components/SpaceModal';

const BrowseSpaces = ({ setCurrentView }) => {
    const [selectedSpace, setSelectedSpace] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [filteredSpaces, setFilteredSpaces] = useState([]);

    const allSpaces = [
        {
            id: 1,
            title: "Modern Gallery Space",
            category: "Gallery/Exhibition",
            location: "Auckland CBD",
            price: 150,
            capacity: 50,
            amenities: ["WiFi", "AV Equipment", "Climate Control"],
            image: "üé®"
        },
        {
            id: 2,
            title: "Industrial Workshop Space",
            category: "Workshop/Class Space",
            location: "Ponsonby",
            price: 80,
            capacity: 20,
            amenities: ["WiFi", "Parking", "Tools"],
            image: "üî®"
        },
        {
            id: 3,
            title: "Boutique Retail Pop-up",
            category: "Retail Pop-Up",
            location: "Newmarket",
            price: 200,
            capacity: 30,
            amenities: ["WiFi", "Security System", "Display Units"],
            image: "üè™"
        },
        {
            id: 4,
            title: "Creative Studio Space",
            category: "Workshop/Class Space",
            location: "Parnell",
            price: 120,
            capacity: 25,
            amenities: ["WiFi", "Natural Light", "Parking"],
            image: "üé≠"
        },
        {
            id: 5,
            title: "Event Hall",
            category: "Event Hall",
            location: "Auckland CBD",
            price: 300,
            capacity: 100,
            amenities: ["WiFi", "AV Equipment", "Catering Kitchen"],
            image: "üé™"
        },
        {
            id: 6,
            title: "Yoga Studio",
            category: "Yoga/Wellness Studio",
            location: "Grey Lynn",
            price: 90,
            capacity: 15,
            amenities: ["Sound System", "Mirrors", "Mats"],
            image: "üßò"
        }
    ];

    const [spaces, setSpaces] = useState(allSpaces);

    const handleSearch = (searchData) => {
        let filtered = allSpaces;

        if (searchData.location) {
            filtered = filtered.filter(space =>
                space.location.toLowerCase().includes(searchData.location.toLowerCase())
            );
        }

        if (searchData.category) {
            filtered = filtered.filter(space => space.category === searchData.category);
        }

        if (searchData.priceRange) {
            const [min, max] = searchData.priceRange.includes('+')
                ? [parseInt(searchData.priceRange), Infinity]
                : searchData.priceRange.split('-').map(Number);
            filtered = filtered.filter(space => space.price >= min && space.price <= max);
        }

        setSpaces(filtered);
    };

    const handleSpaceClick = (space) => {
        setSelectedSpace(space);
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setSelectedSpace(null);
    };

    return (
        <div style={{ minHeight: '100vh', backgroundColor: '#f8f9fa' }}>
            {/* Hero Section with Search */}
            <div style={{
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                padding: '4rem 1rem',
                color: 'white',
                textAlign: 'center'
            }}>
                <h1 style={{ fontSize: '3rem', fontWeight: 'bold', marginBottom: '1rem' }}>
                    Browse Amazing Spaces
                </h1>
                <p style={{ fontSize: '1.2rem', opacity: '0.9', marginBottom: '2rem' }}>
                    Discover the perfect venue for your next project
                </p>
            </div>

            <div style={{ padding: '0 1rem' }}>
                <SearchBar onSearch={handleSearch} />
            </div>

            <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem 1rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
                    <h2 style={{ fontSize: '1.5rem', color: '#2d3748' }}>
                        {spaces.length} spaces available
                    </h2>
                    <select style={{
                        padding: '0.5rem',
                        border: '1px solid #e2e8f0',
                        borderRadius: '6px'
                    }}>
                        <option>Sort by: Recommended</option>
                        <option>Price: Low to High</option>
                        <option>Price: High to Low</option>
                        <option>Rating</option>
                    </select>
                </div>

                <div style={{
                    display: 'grid',
                    gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
                    gap: '2rem'
                }}>
                    {spaces.map((space) => (
                        <div
                            key={space.id}
                            onClick={() => handleSpaceClick(space)}
                            style={{
                                backgroundColor: 'white',
                                borderRadius: '12px',
                                padding: '1.5rem',
                                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                                border: '1px solid #e2e8f0',
                                cursor: 'pointer',
                                transition: 'transform 0.2s ease, box-shadow 0.2s ease'
                            }}
                            onMouseEnter={(e) => {
                                e.target.style.transform = 'translateY(-2px)';
                                e.target.style.boxShadow = '0 8px 15px rgba(0, 0, 0, 0.1)';
                            }}
                            onMouseLeave={(e) => {
                                e.target.style.transform = 'translateY(0)';
                                e.target.style.boxShadow = '0 4px 6px rgba(0, 0, 0, 0.1)';
                            }}
                        >
                            <div style={{
                                height: '200px',
                                backgroundColor: '#f7fafc',
                                borderRadius: '8px',
                                marginBottom: '1rem',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                fontSize: '4rem'
                            }}>
                                {space.image}
                            </div>

                            <h3 style={{
                                fontSize: '1.25rem',
                                fontWeight: 'bold',
                                marginBottom: '0.5rem',
                                color: '#2d3748'
                            }}>
                                {space.title}
                            </h3>

                            <p style={{
                                color: '#667eea',
                                fontSize: '0.9rem',
                                marginBottom: '0.5rem'
                            }}>
                                {space.category}
                            </p>

                            <p style={{
                                color: '#718096',
                                fontSize: '0.9rem',
                                marginBottom: '1rem'
                            }}>
                                üìç {space.location}
                            </p>

                            <div style={{ marginBottom: '1rem' }}>
                                {space.amenities.map((amenity, i) => (
                                    <span key={i} style={{
                                        backgroundColor: '#edf2f7',
                                        color: '#4a5568',
                                        padding: '0.25rem 0.5rem',
                                        borderRadius: '4px',
                                        fontSize: '0.75rem',
                                        marginRight: '0.5rem',
                                        marginBottom: '0.5rem',
                                        display: 'inline-block'
                                    }}>
                                        {amenity}
                                    </span>
                                ))}
                            </div>

                            <div style={{
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'center'
                            }}>
                                <div>
                                    <span style={{
                                        fontSize: '1.5rem',
                                        fontWeight: 'bold',
                                        color: '#2d3748'
                                    }}>
                                        ${space.price}
                                    </span>
                                    <span style={{ color: '#718096', fontSize: '0.9rem' }}>/day</span>
                                </div>
                                <span style={{ color: '#718096', fontSize: '0.9rem' }}>
                                    Capacity: {space.capacity}
                                </span>
                            </div>
                        </div>
                    ))}
                </div>

                {spaces.length === 0 && (
                    <div style={{
                        textAlign: 'center',
                        padding: '4rem 2rem',
                        color: '#666'
                    }}>
                        <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üîç</div>
                        <h3 style={{ fontSize: '1.5rem', marginBottom: '0.5rem' }}>No spaces found</h3>
                        <p>Try adjusting your search filters or browse all spaces</p>
                        <button
                            onClick={() => setSpaces(allSpaces)}
                            style={{
                                marginTop: '1rem',
                                backgroundColor: '#667eea',
                                color: 'white',
                                padding: '0.75rem 1.5rem',
                                border: 'none',
                                borderRadius: '6px',
                                cursor: 'pointer'
                            }}
                        >
                            Show All Spaces
                        </button>
                    </div>
                )}
            </div>

            <SpaceModal
                space={selectedSpace}
                isOpen={isModalOpen}
                onClose={closeModal}
                setCurrentView={setCurrentView}
            />
        </div>
    );
};

export default BrowseSpaces;
