import React, { useState } from 'react';
import Header from './components/Header';
import Hero from './components/Hero';
import BrowseSpaces from './pages/BrowseSpaces';
import './App.css';

function App() {
    const [currentView, setCurrentView] = useState('home');  // ← home or browse

    return (
        <div className="App">
            <Header setCurrentView={setCurrentView} />

            {currentView === 'home' && (
                <>
                    <Hero />
                    <main style={{ padding: '2rem', textAlign: 'center' }}>
                        <h2>Featured Spaces</h2>
                        {/* …your featured‐spaces grid… */}
                    </main>
                </>
            )}

            {currentView === 'browse' && (
                <BrowseSpaces setCurrentView={setCurrentView} />
            )}
        </div>
    );
}

export default App;
