import React from 'react';

const Header = ({ currentView, setCurrentView }) => {
    return (
        <header style={{
            backgroundColor: '#f8f9fa',
            padding: '1rem',
            borderBottom: '1px solid #dee2e6',
            position: 'sticky',
            top: 0,
            zIndex: 1000
        }}>
            <div style={{
                maxWidth: '1200px',
                margin: '0 auto',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
            }}>
                <div
                    style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', cursor: 'pointer' }}
                    onClick={() => setCurrentView('home')}
                >
                    <div style={{
                        width: '40px',
                        height: '40px',
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                        borderRadius: '8px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        color: 'white',
                        fontWeight: 'bold',
                        fontSize: '16px'
                    }}>
                        4ï¿½
                    </div>
                    <span style={{ fontSize: '24px', fontWeight: 'bold', color: '#333' }}>
                        Four Ones
                    </span>
                </div>

                <nav style={{ display: 'flex', gap: '2rem' }}>
                    <button
                        onClick={() => setCurrentView('browse')}
                        style={{
                            background: 'none',
                            border: 'none',
                            cursor: 'pointer',
                            fontSize: '16px',
                            color: currentView === 'browse' ? '#667eea' : '#333',
                            fontWeight: currentView === 'browse' ? '600' : '400'
                        }}
                    >
                        Browse Spaces
                    </button>
                    <button
                        onClick={() => setCurrentView('how-it-works')}
                        style={{
                            background: 'none',
                            border: 'none',
                            cursor: 'pointer',
                            fontSize: '16px',
                            color: currentView === 'how-it-works' ? '#667eea' : '#333',
                            fontWeight: currentView === 'how-it-works' ? '600' : '400'
                        }}
                    >
                        How It Works
                    </button>
                    <button
                        onClick={() => setCurrentView('host')}
                        style={{
                            background: 'none',
                            border: 'none',
                            cursor: 'pointer',
                            fontSize: '16px',
                            color: currentView === 'host' ? '#667eea' : '#333',
                            fontWeight: currentView === 'host' ? '600' : '400'
                        }}
                    >
                        List Your Space
                    </button>
                </nav>

                <div style={{ display: 'flex', gap: '1rem' }}>
                    <button
                        onClick={() => setCurrentView('login')}
                        style={{
                            background: 'none',
                            border: 'none',
                            cursor: 'pointer',
                            fontSize: '16px',
                            color: currentView === 'login' ? '#667eea' : '#333',
                            fontWeight: currentView === 'login' ? '600' : '400'
                        }}
                    >
                        Sign In
                    </button>
                    <button
                        onClick={() => setCurrentView('signup')}
                        style={{
                            backgroundColor: '#667eea',
                            color: 'white',
                            border: 'none',
                            padding: '8px 16px',
                            borderRadius: '6px',
                            cursor: 'pointer',
                            fontSize: '16px',
                            fontWeight: '500'
                        }}
                    >
                        Sign Up
                    </button>
                </div>
            </div>
        </header>
    );
};

export default Header;
